def day_of_year(date): 
    return date.timetuple().tm_yday 

today = datetime.today() 
now = datetime.now()
year = now.year
month = now.month
currentday = now.day

def totalminutes(mytime):
    hours, minutes = map(int, mytime.split(":"))
    return 60*hours+minutes


